var firstUpper=function(val){if(val==null){return""}var f=val.substr(0,1).toUpperCase();return f+val.substring(1,val.length)};var tablename2ClassName=function(tablename){var parts=tablename.split("_");var className="";for(var i=0,max=parts.length;i<max;i++){className+=firstUpper(parts[i])}return className};var underlineToCamelCase=function(val){var parts=val.split("_");var CamelCase="";for(var i=0,max=parts.length;i<max;i++){CamelCase+=firstUpper(parts[i])}return CamelCase.substr(0,1).toLocaleLowerCase()+CamelCase.substring(1,CamelCase.length)};var replaceKeys=function(strtemplate,classInfo){var rst=strtemplate;for(k in classInfo){var re=new RegExp("@@"+k+"@@","gm");rst=rst.replace(re,classInfo[k])}return rst};var toDD=function(val){return(val<10?"0":"")+val.toString()};var now_chnstr=function(){var d=new Date();var yyyy=d.getFullYear();var mm=d.getMonth()+1;var dd=d.getDate();var hh=d.getHours();var mi=d.getMinutes();var ss=d.getSeconds();var rst=yyyy.toString()+"-"+toDD(mm)+"-"+toDD(dd)+" "+toDD(hh)+":"+toDD(mi)+":"+toDD(ss);return rst};
